INSERT INTO convocatorias VALUES("4","","Apoyo a Iniciativas Culturales de Mujeres Indígenas","convocatorias/2012-09-11/4.pdf","Apoyo a Iniciativas Culturales de Mujeres Indígenas.\n\nEn esta convocatoria se abre como una oportunidad para las mujeres indígenas veracruzanas que actualmente estén desarrollando proyectos culturales. El objetivo de esta convocatoria es fortalecer las manifestaciones que constituyen la diversidad cultural y la creatividad popular del Estado de Veracruz vista desde una perspectiva de género; la mujer veracruzana.","2012-09-03","2012-12-31","convocatorias/2012-12-18/4_r.pdf","","1","");
INSERT INTO convocatorias VALUES("5","","Apoyo a Talleres de Laudería Tradicional ","convocatorias/2012-09-11/5.pdf","Apoyo a Talleres de Laudería Tradicional.\n\nEste programa se lanza con el objetivo de fortalecer la producción de instrumentos musicales tradicionales de las diferentes regiones de la entidad veracruzana. ","2012-09-03","2012-12-31","convocatorias/2012-12-18/5_r.jpg","","1","");
INSERT INTO convocatorias VALUES("6","","Programa Editorial 2012","convocatorias/2012-09-11/6.pdf","Programa Editorial 2012.\n\nEste programa se lanza con la intención de impulsar el desarrollo del conocimiento y así apoyar a los creadores de obras literarias, científicas, artísticas y/o académicas. ","2012-09-03","2012-10-26","","","1","");
